INSERT INTO organizations (id, name, description, mission, email, phone, address, city, state, zip_code, verified, type, size, founded_year, created_at) VALUES
  ('org_1', 'Green Portland Initiative', 'A community-focused environmental organization dedicated to creating a more sustainable Portland through education, action, and advocacy.', 'To build a sustainable future for Portland through community engagement and environmental stewardship.', 'info@greenportland.org', '(503) 555-0123', '1234 Eco Street', 'Portland', 'OR', '97201', true, 'NONPROFIT', 'MEDIUM', 2015, NOW() - INTERVAL '2 years'),
  ('org_2', 'Portland Education Alliance', 'Supporting educational excellence and equity in Portland schools through tutoring, mentoring, and resource provision.', 'To ensure every student in Portland has access to quality education and the support they need to succeed.', 'contact@portlandedu.org', '(503) 555-0456', '5678 Learning Lane', 'Portland', 'OR', '97202', true, 'NONPROFIT', 'LARGE', 2008, NOW() - INTERVAL '3 years'),
  ('org_3', 'Helping Hands Network', 'A volunteer-driven organization focused on addressing food insecurity and supporting vulnerable populations in our community.', 'To eliminate hunger and provide support to those in need through community collaboration and compassion.', 'volunteers@helpinghands.org', '(503) 555-0789', '9012 Care Circle', 'Portland', 'OR', '97203', true, 'NONPROFIT', 'SMALL', 2012, NOW() - INTERVAL '1 year'),
  ('org_4', 'Digital Inclusion Network', 'Bridging the digital divide by providing technology education and support to underserved communities.', 'To ensure everyone has access to technology and the skills needed to thrive in the digital age.', 'hello@digitalinclusion.org', '(503) 555-0321', '3456 Tech Avenue', 'Portland', 'OR', '97204', false, 'NONPROFIT', 'SMALL', 2020, NOW() - INTERVAL '6 months'),
  ('org_5', 'Habitat for Humanity Portland', 'Building homes, communities, and hope by bringing people together to create affordable housing solutions.', 'A world where everyone has a decent place to live.', 'info@habitatportland.org', '(503) 555-0654', '7890 Build Boulevard', 'Portland', 'OR', '97205', true, 'NONPROFIT', 'LARGE', 1985, NOW() - INTERVAL '5 years');
INSERT INTO volunteer_opportunities (id, title, description, requirements, responsibilities, benefits, start_date, end_date, location, address, city, state, zip_code, max_spots, skills, categories, status, organization_id, created_at) VALUES
  ('opp_1', 'Community Garden Cleanup', 'Join us for a morning of planting, weeding, and harvesting in our community garden. All skill levels welcome! Tools and refreshments provided.', 'No experience necessary. Must be able to work outdoors and perform light physical activity.', 'Weeding garden beds, planting new vegetables, harvesting produce, general cleanup and maintenance.', 'Learn about sustainable gardening, meet like-minded community members, enjoy fresh air and exercise.', NOW() + INTERVAL '3 days', NOW() + INTERVAL '3 days' + INTERVAL '4 hours', 'Laurelhurst Community Garden', '1234 Garden Way', 'Portland', 'OR', '97214', 25, ARRAY['Gardening', 'Physical Labor'], ARRAY['environment'], 'ACTIVE', 'org_1', NOW() - INTERVAL '2 days'),
